$(document).ready(function(){
	//when hover-in
	$('.bs-footer').on('mouseenter', function(){
		var winInit = $(window).width();
		if(winInit != 483)
			{
				$('.bs-footer').animate({
					bottom: '0%'
				});
			}
	});
	
	//when hover-out
	$('.bs-footer').on('mouseleave', function(){
		var winInit = $(window).width();
		if(winInit != 483)
			{
				$('.bs-footer').animate({
					bottom: '-25%'
				});
			}
	});
	
	
	//mobile responsiveness
	var winInit = $(window).width();
		if(winInit == 483)
		{
			$('.bs-footer').animate({
				bottom: '-69%'
			});
		}
	
	$('.bs-footer').on('mouseenter', function(){
		var winInit = $(window).width();
		if(winInit == 483)
			{
				$('.bs-footer').animate({
					bottom: '0%'
				});
			}
	});
	
	$('.bs-footer').on('mouseleave', function(){
		var winInit = $(window).width();
		if(winInit == 483)
			{
				$('.bs-footer').animate({
					bottom: '-69%'
				});
			}
	});
	
	
	
});