$(document).ready(function(){
	
	function displayTableRow(){
		var win = $(window).width();

		if(win <= 483 )
			{
				if($('.bs-client-portfolio-cards .my-sm-auto').hasClass('col-2'))
					{
						$('.bs-client-portfolio-cards .my-sm-auto').removeClass('col-2');
						$('.bs-client-portfolio-cards .my-sm-auto').addClass('col-4');						
					}
			}
		
		if(win > 483)
					{
						if($('.bs-client-portfolio-cards .my-sm-auto').hasClass('col-4'))
							{
								$('.bs-client-portfolio-cards .my-sm-auto').removeClass('col-4');
								$('.bs-client-portfolio-cards .my-sm-auto').addClass('col-2');	
								
								//
								console.log('m');
							}
						
					}
	}
	
	setInterval(function(){
		displayTableRow();
		
	}, 100);
});