$(document).ready(function(){
	$('.fa.fa-star').click(function(){
			if(!$(this).hasClass('stared'))
				{
					if($(this).hasClass('unstared'))
						{
							$(this).removeClass('unstared');
						}
					$(this).addClass('stared');
				}
			else
				{
					$(this).removeClass('stared');
					$(this).addClass('unstared');
				}
	});
});