$(document).ready(function(){
	//dismiss modal
	var md = $('.modal-footer').find('.btn').attr('data-dismiss');
	$('.close-request').click(function(){
		$('.'+md+'').css({'zoom': '.2', 'transition':'3s'}).delay(3000).hide();
	});
	
	$('.close').click(function(){
		$('.'+md+'').css({'zoom': '.2', 'transition':'3s'}).delay(3000).hide();
	});
	
	//show the modal
	$(document).find('.btn-outline-info').click(function(){
		$('.'+md+'').show().delay(2000).css({'zoom':'.9', 'transition':'3s'});
	});
});