$(document).ready(function(){
	let bsd = new ibox();
	let template = {
		getTemplate: function(){
			return (
				`<div class="row" style="margin-top:3rem;">
    <div class="col">
        <div class="card bg-light shadow-lg add-service-card">
            <div class="card-header border rounded-0" style="background-color: #464a6c;color: #f5f5f5;">
                <h5 class="mb-0">Add Service Listing Item(s)</h5>
            </div>
            <div class="card-body bg-light border rounded border-white shadow">
                <form id="add-service-item-form">
                    <div class="form-group">
                        <div class="form-row">
                            <div class="col"><input type="text" name="service_name" required placeholder="Service Name" class="form-control" id="serviceName" /></div>
                            <div class="col"><input type="number" name="service_price" required placeholder="Service Price" min="1" class="form-control" id="servicePrice" /></div>
                            <div class="col"><select name="service_type" required class="form-control" id="serviceType"><option value="Mobile Service">Mobile Service</option><option value="Stationary Service">Stationary Service</option></select></div>
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="form-row">
                            <div class="col-5"><textarea rows="3" required name="service_description" placeholder="Service Description (Required)" class="form-control" id="serviceDescription"></textarea></div>
                            <div class="col">
                                <div align="center" style="padding: 30px;"><button class="btn btn-outline-primary btn-lg border rounded shadow add-service-btn" type="submit">Add</button></div>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>`
			);
		}
	};
	//add servies Modal
	let addServiceMd = function(){
		$('.create-service-item').click(function(){		
		
		bsd.content_set(template.getTemplate());
		bsd.loader_show();
		bsd.open();
		bsd.loader_hide();
		
		});
	};
	
	//run app
	function run(){
		//call the add service func
		addServiceMd();
	}
	
	run();
});