$(document).ready(function(){
	//the initial widnow width
	var winInit = $(window).width();
	/*
		* when the document height is 0 
		* set the side-col-items to 6rem
		* otherwise 0rem
	*/
	$(document).scroll(function(){
			var doch = $(document).scrollTop();
			
			if(doch > 0)
				{
					if($('.bs-col-item').hasClass('bs-toggle-margin-top-6'))
						{
							$('.bs-col-item').removeClass('bs-toggle-margin-top-6');
							$('.bs-col-item').addClass('bs-toggle-margin-top-0');
						}
					
				}
		if(doch <= 0)
				{
					if($('.bs-col-item').hasClass('bs-toggle-margin-top-0'))
						{
							$('.bs-col-item').removeClass('bs-toggle-margin-top-0');
							$('.bs-col-item').addClass('bs-toggle-margin-top-6');
						}
					
				}	
	});
	
	// var winInit = $(window).width();
	// alert(winInit);
	/**
	*toggle the sidebar when in mobile view
	*/
	function toggleSideBar(){
		var winInit = $(window).width();
		if(winInit == 483)
		{
			$('.toggle-sidebar').show();
		}
	
	if(winInit == 768)
		{
			$('.toggle-sidebar').show();
		}
		
		if(winInit == 320)
		{
			$('.toggle-sidebar').show();
		}
		
		if(winInit == 375)
		{
			$('.toggle-sidebar').show();
		}
		
		if(winInit == 425)
		{
			$('.toggle-sidebar').show();
		}
		
	if(winInit >= 1024)
		{
			$('.toggle-sidebar').hide();
		}
		
		if(winInit == 608)
		{
			$('.toggle-sidebar').show();
		}
		if(winInit == 677)
		{
			$('.toggle-sidebar').show();
		}
		
		
		if(winInit == 520)
		{
			$('.toggle-sidebar').show();
		}
		
		
		if(winInit == 864)
		{
			$('.toggle-sidebar').show();
		}
		
		
		if(winInit == 731)
		{
			$('.toggle-sidebar').show();
		}
		
		if(winInit <= 966)
		{
			$('.toggle-sidebar').show();
		}
		
	}
	
	
	setInterval(function(){
		
		toggleSideBar();
		
	}, 100);
	
	$('.toggle-sidebar').click(function(){
		
		var winInit = $(window).width();
		
		if(winInit !== 483 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '+=26%'
			});
		}
		
		if(winInit == 483 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '+=50%'
			});
		}
		
		if(winInit == 360 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '+=70%'
			});
		}
	});
	
	//hide sidebar when click on content contex
	$('.content-contex').click(function(){
		
		var winInit = $(window).width();
		
		if(winInit !== 483 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '-=26%'
			});
		}
		
		if(winInit == 483 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '-=50%'
			});
		}
		
		if(winInit == 360 && winInit <= 966)
		{
			$('.sidebar').animate({
				right: '-=70%'
			});
		}
		
	});
	
});